mapper.select:
select count(*)
from comments3
where board_num = #{board_num}

select *
from (select rownum rn, q.*
      from (select *
            from comments3
            where board_num = #{board_num}
            order by reg_date desc) q
      where rownum <= #{end})
where rn >= #{start}
  and rn <= #{end}

mapper.update:
update comments3
set content = #{content}
where num = #{num}
